import React, { Component } from "react";
import Launch from "./Launch";
import { Dropdown, Loader, Container } from "semantic-ui-react";

const options = [
  { key: 1, text: "5", value: 5 },
  { key: 2, text: "10", value: 10 },
  { key: 3, text: "25", value: 25 }
];

class List extends Component {
  state = {
    launches: [],
    n: 5,
    isLoading: true
  };

  loadData = async () => {
    try {
      let res = await fetch("/upcoming/" + this.state.n);
      let data = await res.json();
      this.setState({ launches: data.launches, isLoading: false });
    } catch (e) {
      console.log("server not responding");
    }
  };

  handleChange = (e, data) => {
    this.setState({ n: data.value, isLoading: true }, () =>
      this.loadData()
    );
  };

  componentDidMount() {
    this.loadData();
  }

  render() {
    return (
      <Container>
        <div>
          I want to see
          <Dropdown onChange={this.handleChange} clearable options={options} selection />
          recent launches
        </div>

        {this.state.isLoading ? (
          <Loader active inline="centered" />
        ) : (
          this.state.launches.map((launch, i) => <Launch key={i} {...launch} />)
        )}
      </Container>
    );
  }
}

export default List;
