import React, { Component } from "react";
import Launch from "./Launch";

class List extends Component {
  state = {
    launches: [],
    n: 5,
    isLoading: true
  };

  loadData = async () => {
    try {
      let res = await fetch("/upcoming/" + this.state.n);
      let data = await res.json();
      setTimeout(
        this.setState({ launches: data.launches, isLoading: false }),
        5000
      );
    } catch (e) {
      console.log("server not responding");
    }
  };

  handleChange = e => {
    this.setState({ n: e.target.value }, () => this.loadData());
  };

  componentDidMount() {
    this.loadData();
  }

  render() {
    return (
      <div>
        <div>
          I want to see
          <select defaultValue={this.state.n} onChange={this.handleChange}>
            <option value="5">5</option>
            <option value="10">10</option>
            <option value="20">20</option>
            <option value="50">50</option>
          </select>
          recent launches
        </div>

        {this.state.isLoading ? (
          <img alt="loading" src="/loading.gif" />
        ) : (
          this.state.launches.map((launch, i) => <Launch key={i} {...launch} />)
        )}
      </div>
    );
  }
}

export default List;
